mapper.select:
select pass
from tbl_emp
where empno=#{empno}

select empno
from tbl_emp
where empno=#{empno} and pass=#{pass}

select empno, ename, deptno, position, regdate, dname
from tbl_emp natural join tbl_dept
where empno=#{empno}

select *
from tbl_emp
where empno=#{empno} and ename=#{ename} and email=#{email}
